const event = $('Called by Parent').item.json;
const config = $('Get Seller Config').first().json;
const item = $('Get Item ID from SKU').first().json.items[0];


const eventDate = new Date(event.event_timestamp_utc)
  .toISOString()
  .split('T')[0];

const refNumber = `${event.order_item_id}(Order: ${event.order_id})`;

return {
  json: {
    customer_id: config.CustomerID,
    date: eventDate,
    salesorder_number: refNumber,
    reference_number: refNumber,
    is_inclusive_tax: true,
    location_id: config.ParentLocationID,
    notes: `Customer name: ${event.customer}`,
    line_items: [{
      item_id: item.item_id,
      name: event.product_title,
      rate: event.selling_price / event.quantity,
      quantity: event.quantity,
      tax_id: config.TaxID,
      item_total: event.selling_price,
      location_id: config.LocationID
    }]
  }
};

